func TestCollectRewards(t *testing.T) {
	reward := core.NewToken(1, common.HexToAddress("0x1f9840a85d5aF5bf1D1762F925BDADdC4201F984"), 18, "r", "reward")
	token0 := core.NewToken(1, common.HexToAddress("0x0000000000000000000000000000000000000001"), 18, "t0", "token0")
	token1 := core.NewToken(1, common.HexToAddress("0x0000000000000000000000000000000000000002"), 18, "t1", "token1")

	pool_0_1, _ := entities.NewPool(token0, token1, constants.FeeMedium, utils.EncodeSqrtRatioX96(constants.One, constants.One), big.NewInt(0), 0, nil)

	incentiveKey := []*IncentiveKey{{
		RewardToken: reward,
		Pool:        pool_0_1,
		StartTime:   big.NewInt(100),
		EndTime:     big.NewInt(200),
		Refundee:    common.HexToAddress("0x0000000000000000000000000000000000000001"),
	}}
	incentiveKeys := append(incentiveKey, &IncentiveKey{
		RewardToken: reward,
		Pool:        pool_0_1,
		StartTime:   big.NewInt(50),
		EndTime:     big.NewInt(100),
		Refundee:    common.HexToAddress("0x0000000000000000000000000000000000000089"),
	})

	recipient := common.HexToAddress("0x0000000000000000000000000000000000000003")
	// sender := common.HexToAddress("0x0000000000000000000000000000000000000004")
	tokenID := big.NewInt(1)

	// succeeds with amount
	params, err := CollectRewards(incentiveKey, &ClaimOptions{
		TokenID:   tokenID,
		Recipient: recipient,
		Amount:    big.NewInt(1),
	})
	assert.NoError(t, err)
	assert.Equal(t, "", hexutil.Encode(params.Calldata))
	assert.Equal(t, "0x00", utils.ToHex(params.Value))

	// succeeds no amount
	params, err = CollectRewards(incentiveKey, &ClaimOptions{
		TokenID:   tokenID,
		Recipient: recipient,
	})
	assert.NoError(t, err)
	assert.Equal(t, "", hexutil.Encode(params.Calldata))
	assert.Equal(t, "0x00", utils.ToHex(params.Value))

	// succeeds multiple keys
	params, err = CollectRewards(incentiveKeys, &ClaimOptions{
		TokenID:   tokenID,
		Recipient: recipient,
	})
	assert.NoError(t, err)
	assert.Equal(t, "", hexutil.Encode(params.Calldata))
	assert.Equal(t, "0x00", utils.ToHex(params.Value))
}

func TestWithdrawToken(t *testing.T) {
	reward := core.NewToken(1, common.HexToAddress("0x1f9840a85d5aF5bf1D1762F925BDADdC4201F984"), 18, "r", "reward")
	token0 := core.NewToken(1, common.HexToAddress("0x0000000000000000000000000000000000000001"), 18, "t0", "token0")
	token1 := core.NewToken(1, common.HexToAddress("0x0000000000000000000000000000000000000002"), 18, "t1", "token1")

	pool_0_1, _ := entities.NewPool(token0, token1, constants.FeeMedium, utils.EncodeSqrtRatioX96(constants.One, constants.One), big.NewInt(0), 0, nil)

	incentiveKey := []*IncentiveKey{{
		RewardToken: reward,
		Pool:        pool_0_1,
		StartTime:   big.NewInt(100),
		EndTime:     big.NewInt(200),
		Refundee:    common.HexToAddress("0x0000000000000000000000000000000000000001"),
	}}
	incentiveKeys := append(incentiveKey, &IncentiveKey{
		RewardToken: reward,
		Pool:        pool_0_1,
		StartTime:   big.NewInt(50),
		EndTime:     big.NewInt(100),
		Refundee:    common.HexToAddress("0x0000000000000000000000000000000000000089"),
	})

	recipient := common.HexToAddress("0x0000000000000000000000000000000000000003")
	sender := common.HexToAddress("0x0000000000000000000000000000000000000004")
	tokenID := big.NewInt(1)

	// succeeds with one keys
	params, err := WithdrawToken(incentiveKey, &FullWithdrawOptions{
		ClaimOptions: ClaimOptions{
			TokenID:   tokenID,
			Recipient: recipient,
			Amount:    big.NewInt(0),
		},
		WithdrawOptions: WithdrawOptions{
			Owner: sender,
			Data:  common.FromHex("0x0000000000000000000000000000000000000008"),
		},
	})

	assert.NoError(t, err)
	assert.Equal(t, "", hexutil.Encode(params.Calldata))
	assert.Equal(t, "0x00", utils.ToHex(params.Value))

	// succeeds with multiple keys
	params, err = WithdrawToken(incentiveKeys, &FullWithdrawOptions{
		ClaimOptions: ClaimOptions{
			TokenID:   tokenID,
			Recipient: recipient,
			Amount:    big.NewInt(0),
		},
		WithdrawOptions: WithdrawOptions{
			Owner: sender,
			Data:  common.FromHex("0x0000000000000000000000000000000000000008"),
		},
	})
	assert.NoError(t, err)
	assert.Equal(t, "", hexutil.Encode(params.Calldata))
	assert.Equal(t, "0x00", utils.ToHex(params.Value))
}

func TestEncodeDeposit(t *testing.T) {
	reward := core.NewToken(1, common.HexToAddress("0x1f9840a85d5aF5bf1D1762F925BDADdC4201F984"), 18, "r", "reward")
	token0 := core.NewToken(1, common.HexToAddress("0x0000000000000000000000000000000000000001"), 18, "t0", "token0")
	token1 := core.NewToken(1, common.HexToAddress("0x0000000000000000000000000000000000000002"), 18, "t1", "token1")

	pool_0_1, _ := entities.NewPool(token0, token1, constants.FeeMedium, utils.EncodeSqrtRatioX96(constants.One, constants.One), big.NewInt(0), 0, nil)
	recipient := common.HexToAddress("0x0000000000000000000000000000000000000003")
	sender := common.HexToAddress("0x0000000000000000000000000000000000000004")
	tokenID := big.NewInt(1)

	incentiveKey := []*IncentiveKey{{
		RewardToken: reward,
		Pool:        pool_0_1,
		StartTime:   big.NewInt(100),
		EndTime:     big.NewInt(200),
		Refundee:    common.HexToAddress("0x0000000000000000000000000000000000000001"),
	}}
	incentiveKeys := append(incentiveKey, &IncentiveKey{
		RewardToken: reward,
		Pool:        pool_0_1,
		StartTime:   big.NewInt(50),
		EndTime:     big.NewInt(100),
		Refundee:    common.HexToAddress("0x0000000000000000000000000000000000000089"),
	})

	// succeeds single key
	deposit, err := EncodeDeposit(incentiveKey)
	if err != nil {
		t.Fatal(err)
	}
	assert.Equal(t, "", hexutil.Encode(deposit))

	// succeeds multiple keys
	deposit, err = EncodeDeposit(incentiveKeys)
	if err != nil {
		t.Fatal(err)
	}
	assert.Equal(t, "", hexutil.Encode(deposit))

	// safeTransferFrom with correct data for staker
	deposit, err = EncodeDeposit(incentiveKey)
	if err != nil {
		t.Fatal(err)
	}
	options := &SafeTransferOptions{
		Sender:    sender,
		Recipient: recipient,
		TokenID:   tokenID,
		Data:      deposit,
	}
	params, err := SafeTransferFromParameters(options)
	if err != nil {
		t.Fatal(err)
	}
	assert.Equal(t, "", hexutil.Encode(params.Calldata))
	assert.Equal(t, "0x00", utils.ToHex(params.Value))
}
