	params, err = SwapCallParameters([]*entities.Trade{trade1, trade2}, &SwapOptions{
		SlippageTolerance: slippageTolerance,
		Recipient:         recipient,
		Deadline:          deadline,
		Fee: &FeeOptions{
			Fee:       core.NewPercent(big.NewInt(5), big.NewInt(1000)),
			Recipient: recipient,
		},
	})
	if err != nil {
		t.Fatal(err)
	}
	assert.Equal(t, "", hexutil.Encode(params.Calldata))
	assert.Equal(t, "0xd0", utils.ToHex(params.Value))

	// fee
	r1, _ = entities.NewRoute([]*entities.Pool{pool_0_1, pool_1_3}, token0, token3)
	trade1, _ = entities.FromRoute(r1, core.FromRawAmount(token0, big.NewInt(100)), core.ExactInput)
